import React, {useEffect, useRef, useState} from 'react';
import * as d3 from "d3";








export default function D3Test(props){
    const d3Container = useRef(null);
    useEffect(()=>{
        const width = 300
        const height = 300
        const svg = d3.select(d3Container.current).attr("viewbox",[0, 0, width, height])
        svg.selectAll("circle").data([10,20,50, 100]).join("line").attr("y1",150).attr("y2",150).attr("x1",d=>d).attr("x2",d=>d+10).attr("stroke","black")
    }, [])

    return <svg
        className="d3_test"

        ref={d3Container}
    />
}
