import React, {useEffect, useRef, useState} from 'react';
import * as d3 from "d3";








export default function D3Test(props){
    const d3Container = useRef(null);
    useEffect(()=>{
        const width = 300
        const height = 300
        const svg = d3.select(d3Container.current).attr("viewbox",[0, 0, width, height]).attr("width",width).attr("height",height)

        var y_scale = d3.scaleLinear().domain([10,100]).range([0,height])

        svg.selectAll("circle").data([10,20,50, 100])
            .join("line")
            .attr("x1",150)
            .attr("x2",250)
            .attr("y1",d=>y_scale(d))
            .attr("y2",d=>y_scale(d))
            .attr("stroke","black")
        var y_axis = d3.axisLeft().scale(y_scale)
        svg.append("g").attr("transform","translate(50,10)").call(y_axis)
    }, [])

    return <svg
        className="d3_test"

        ref={d3Container}
    />
}
