import React, {useEffect, useRef, useState} from 'react';
import * as d3 from "d3";








export default function D3Test(props){
    const d3Container = useRef(null);
    useEffect(()=>{
        const width = 300
        const height = 300
        const svg = d3.select(d3Container.current).attr("viewbox",[0, 0, width, height]).attr("width",width).attr("height",height)

        var y_scale = d3.scaleLinear().domain([10,100]).range([0,height]).nice()
        function formatTick(d) {
            const s = (d / 1e6).toFixed(1);
            return this.parentNode.nextSibling ? `\xa0${s}` : `$${s} million`;
        }
        // var y_axis = d3.axisRight().scale(y_scale).ticks(15).tickSize(width).tickFormat(formatTick)
        var yAxis = g => g
            .attr("transform", `translate(0,0)`)
            .call(d3.axisLeft(y)
                .ticks(24)
                .tickFormat(formatHours)
                .tickSize(-width + margin.left + margin.right)
                .tickPadding(10))
            .call(g => g.selectAll(".domain, .tick:first-of-type, .tick:last-of-type").remove())
            .call(g => g.selectAll(".tick line").attr("stroke", "#fff").attr("stroke-width", 0.5));




        svg.selectAll("circle").data(y_scale.ticks())
            .join("line")
            .attr("x1",150)
            .attr("x2",250)
            .attr("y1",d=>y_scale(d))
            .attr("y2",d=>y_scale(d))
            .attr("stroke","black")
        svg.append("g").attr("transform","translate(50,10)").call(y_axis)
        console.log(y_axis.ticks())
    }, [])

    return <svg
        className="d3_test"

        ref={d3Container}
    />
}
