import React, {useEffect, useRef, useState} from 'react';
import * as d3 from "d3";








export default function D3Test(props){
    const d3Container = useRef(null);

    const width = 900
    const height = 300
    const marging = {top:10, left:10, right:10,bottom:10}
    useEffect(()=>{

        const svg = d3.select(d3Container.current).attr("viewbox",[0, 0, width-marging.right - marging.left, height-marging.bottom - marging.top]).attr("width",width).attr("height",height)

        var y_scale = d3.scaleLinear().domain([10,100]).range([marging["top"],height-marging["bottom"]]).nice()

        var y_axis = d3.axisRight().scale(y_scale).ticks(15).tickSize(width)

        var x_scale = d3.scaleTime().domain([new Date("2018-01-01T00:00:00"),new Date("2018-01-03T00:00:00")]).range([marging["left"], width-marging["right"]])

        var x_axis = d3.axisBottom().scale(x_scale)

        // svg.selectAll("circle").data(y_scale.ticks())
        //     .join("line")
        //     .attr("x1",150)
        //     .attr("x2",250)
        //     .attr("y1",d=>y_scale(d))
        //     .attr("y2",d=>y_scale(d))
        //     .attr("stroke","black")

        svg.append("g")
            .attr("stroke-opacity",0.5)
            .attr("stroke-dasharray","2,2")
            .attr("transform",`translate(${marging.left},${marging.top})`).call(y_axis)
        svg.append("g")
            .attr("transform", `translate(${marging.left},${height })`)
            .call(x_axis)
        svg.selectAll(".tick text")
            .attr("x", 4).attr("dy", -4);
    }, [])

    return <svg width={width+marging.bottom } height={height + marging.right + marging.left}
        className="d3_test"

        ref={d3Container}
    />
}
